const styles = {
  wrapper: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#FFFEAB',
    alignItems: 'center',
    fontFamily: 'Montserrat',
  },
  body: {
    flex: 1,
    alignItems: 'center',
    paddingBottom: 20,
  },
  logoImage: {
    marginBottom: 15,
    width: 125,
    height: 125,
    borderRadius: 70,
  },
  userName: {
    fontSize: 14,
  },
  rate: {
    fontWeight: 'bold',
  },
  titleText: {
    fontWeight: 'bold',
    fontSize: 20,
    marginTop: 15,
  },
  atividadesContainer: {
    width: '80%',
  },
};

export default styles;
